
import { useEffect, useState } from 'react';
import { CircularProgress, FormHelperText } from '@mui/material';
import detectEthereumProvider from '@metamask/detect-provider';
import LoadingIcon from '@mui/material/CircularProgress';
import Button from './Button';
import Link from '@mui/material/Link';
import Modal from '@mui/material/Modal';
import Typography from '@mui/material/Typography';
import CheckIcon from '@mui/icons-material/Check';
import CloseIcon from '@mui/icons-material/Close';
import Box from '@mui/material/Box';
import styled from '@emotion/styled';
import { useLoadingState } from '../lib/react';
import WalletConnect from '@walletconnect/client';
import QRCodeModal from '@walletconnect/qrcode-modal';
import { getBlockChainName, ChainId } from '../lib/blockchain';
import { getTypedSignatureMessage } from '../lib/blockchain/signature';

const metamaskImage = 'data:image/svg+xml;base64,';
const walletConnectImage = 'data:image/svg+xml;base64,';

interface WalletState {
  address?: string;
  approved: boolean;
  signature?: string;
  connected: boolean;
  error: string;
  loading: boolean;
}

const ModalBox = styled(Box)`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 600px;
  background: white;
  border-radius: 20px;
`;

export default function WalletConnectButton ({ email, userId, chainId, connect: setWallet, walletState }: { email: string, userId: string, chainId?: number, connect: (a: { address: string, chainId: number, signature?: string }) => void, walletState: WalletState }) {

  const [metamask, setProviderState] = useLoadingState<{ provider: any }>({});
  const [walletError, setWalletError] = useState<string>('');
  const [open, setOpen] = useState(false);

  function openModal () {
    setWalletError('');
    setOpen(true);
  }

  function closeModal () {
    setOpen(false);
  }

  // https://docs.walletconnect.com/1.0/quick-start/dapps/client
  async function activateWalletConnect () {

    // Create a connector
    const connector = new WalletConnect({
      bridge: 'https://bridge.walletconnect.org', // Required
      qrcodeModal: QRCodeModal
    });

    if (!connector.connected) {
      // passing chainId doesnt affect metamask but might be useful for others
      await connector.createSession({
        chainId,
      });
    }
    else {
      const chainIdToRequest = chainId || connector.chainId;
      const { signature, error: _error } = await requestSignature(connector, { chainId: chainIdToRequest, email, userId, from: connector.accounts[0] });
      if (signature) {
        setWallet({ address: connector.accounts[0], chainId: connector.chainId, signature });
      }
      else {
        setWalletError(_error);
      }
    }

    // Subscribe to connection events
    connector.on('connect', async (error, payload) => {
      if (error) {
        setWalletError(error.message);
        return;
      }
      setWalletError('');
      // Get provided accounts and chainId
      const { accounts, chainId } = payload.params[0];
      if (accounts[0]) {
        const { signature, error } = await requestSignature(connector, { chainId, email, userId, from: accounts[0] });
        if (signature) {
          setWallet({ address: accounts[0], chainId, signature });
        }
        else {
          setWalletError(error);
        }
      }
    });
    connector.on('session_update', async (error, payload) => {
      if (error) {
        setWalletError(error.message);
        return;
      }
      setWalletError('');
      // Get updated accounts and chainId
      const { accounts, chainId } = payload.params[0];
      const { signature, error: _error } = await requestSignature(connector, { chainId, email, userId, from: accounts[0] });
      if (signature) {
        setWallet({ address: accounts[0], chainId, signature });
      }
      else {
        setWalletError(_error);
      }
    });

    connector.on('disconnect', (error, payload) => {
      if (error) {
        throw error;
      }
      // Delete connector
      setWallet({ address: '', chainId: undefined, signature: undefined });
    });

    // open wallet connect modal
    closeModal();
  }

  function activateMetaMask () {
    metamask.provider
      .request({ method: 'eth_requestAccounts' })
      .then(async (accounts: string[]) => {
        closeModal();
        const chainIdToRequest = chainId || parseInt(metamask.provider.chainId, 16);
        const { signature, error } = await requestSignature(metamask.provider, { chainId: chainIdToRequest, email, userId, from: accounts[0] });
        if (signature) {
          setWallet({
            address: accounts[0],
            signature,
            chainId: parseInt(metamask.provider.chainId, 16),
          });
        }
        else {
          setWalletError(error)
        }
      })
      .catch(err => {
        setWalletError(err.message || 'There was a problem, please check the console');
      });
  }

  useEffect(() => {

    detectEthereumProvider({ mustBeMetaMask: true })
      .then((provider: any) => {

        setProviderState({ loading: false, provider });
        if (provider) {
          // docs: https://docs.metamask.io/guide/ethereum-provider.html#methods
          provider.on('chainChanged', (chainId: string) => {
            window.location.reload();
          });

          provider.on('accountsChanged', async (accounts: string[]) => {
            if (accounts[0]) {
              console.log('requestSignature: accountsChanged');
            }
            else {
              setWallet({ address: '', chainId: undefined, signature: undefined });
            }
          });
        }
      });
  }, []);

  useEffect(() => {
    setWalletError('');
  }, [walletState]);

  if (metamask.loading) {
    return (
        <Button disabled variant='outlined' size='large' sx={{ width: '100%' }}>
          <LoadingIcon color='secondary' size='2em' />
        </Button>
      );
  }

  return (
    <>
      <Box sx={{ textAlign: 'center' }}>
        {(
          (metamask.provider && walletState.address)
          ? (
            <Button
              endIcon={walletState.loading
                ? <CircularProgress size={20} sx={{ color: '#ccc' }} />
                : walletState.approved ? (
                  <Box display='flex' alignItems='center'>
                    <Typography color='success' sx={{ m: 0, fontSize: '12px !important' }}>{walletState.connected ? 'connected' : 'approved'}</Typography>
                    <CheckIcon />
                  </Box>
                ) : null}
              color={walletState.approved ? 'success' : (walletState.error ? 'error' : 'inherit')}
              onClick={openModal}
              sx={{ textAlign: 'center', justifyContent: 'space-between', width: '100%' }} variant='outlined' size='large'
            >
              {shortenedContractAddresss(walletState.address)}
            </Button>
          ) : (
            <Button onClick={openModal} variant='outlined' size='large'>
              Connect
            </Button>
          )
        )}
        {
          (walletError || walletState.error) && (
            <FormHelperText error sx={{ marginTop: '1em', textAlign: 'center' }}>{walletError || walletState.error}</FormHelperText>
          )
        }
      </Box>
      <Modal open={open} onClose={closeModal}>
        <ModalBox sx={{ p: 4, boxShadow: 24 }}>
          <Box display='flex' justifyContent='space-between'>
            <Typography sx={{ fontSize: 24, fontWeight: 'bold' }}>
              Connect to a wallet
            </Typography>
            <CloseIcon style={{ cursor: 'pointer' }} onClick={closeModal} />
          </Box>
          <Box display='flex' justifyContent='space-between' sx={{ my: 2 }}>
            <Button variant='outlined' onClick={activateMetaMask} sx={{ display: 'block', textAlign: 'center', pt: 4, pb: 2, width: '48%' }}>
              <img src={metamaskImage} height={48} width={48} style={{  }} /><br />
              MetaMask
            </Button>
            <Button variant='outlined' onClick={activateWalletConnect} sx={{ display: 'block', textAlign: 'center', pt: 4, pb: 2, width: '48%' }}>
              <img src={walletConnectImage} height={48} width={48} style={{  }} /><br />
              Wallet Connect
            </Button>
          </Box>
          <Typography align='center' sx={{ mt: 4, mb: 2}}>
            New to Ethereum wallets? <Link href='https://ethereum.org/en/wallets/' target='_blank'>Learn more</Link>
          </Typography>
        </ModalBox>
      </Modal>
    </>
  )
}

const shortenedContractAddresss = (address: string) => {
  return address.substring(0, 6) + '...' + address.substring(address.length - 4);
};

interface SignatureResponse {
  error?: string;
  signature?: string;
}

async function requestSignature (provider: any, { from, email, chainId }: { from: string, email: string, userId: string, chainId: number }): Promise<SignatureResponse> {
  const msgParams = getTypedSignatureMessage({ chainId, email });
  try {
    const signature = provider.request
      // injected provider
      ? await provider.request({
        method: 'eth_signTypedData_v4',
        params: [from, JSON.stringify(msgParams)],
      })
      // wallet connect
      : await (provider as WalletConnect).signTypedData([from, JSON.stringify(msgParams)]);
    return { signature };
  }
  catch (err) {
    if (err.code === -32603) {
      return { error: `Please connect to the ${chainId ? getBlockChainName(chainId as ChainId) : 'Ethereum or xDai'} network` };
    }
    return { error: err.message || err };
  }
}
